clc; clear; close all;% Dataset givenx = linspace(-rand(1),rand(1)).'*10;y = randn(1)*cos(x)+randn(1)*sin(x)+randn(size(x))*0.1;% Build your system matrix hereA = [cos(x) sin(x)];% Solve the system, and then unpack the individual values into a and bc = A \ y;a = c(1);b = c(2);% Calculate your fitted y here using cyf = a .* cos(x) + b .* sin(x);% Sum squared errorS = sum((y - yf) .^ 2);% Mean squared error% ... m = number of data points = 100% ... n = number of fit functions / coefficients = 2% ... therefore m - nm = 100;n = 2;meanSquaredError = sqrt(S / (m - n));% Uncertaintyuncertainty = meanSquaredError .* sqrt(inv(A * A.'));% % Estimate the errors in cdc = uncertainty * c;da = dc(1);db = dc(2);% Overlay plot for testingplot(x, y, '.', x, yf, '-');legend('y', sprintf('(%0.4f +/- %0.4f) cos(x) + (%0.4f +/- %0.4f) sin(x)', a, da, b, db));